    <?php
    include("../configuracion.php");
    $nodoc = !empty($_POST['token']) ? $_POST['token'] : (!empty($_GET['token']) ? $_GET['token'] : NULL);

    // Consulta SQL para obtener los datos de la tabla
    $sql = "SELECT * FROM licenciasguanajuato2 WHERE Nlicencia ='$nodoc'";
    $resultado = $link->query($sql);

    // Verificar si hay datos en la tabla
    if ($resultado->num_rows > 0) {
        // Si hay datos, mostrar la lista de información
        while ($fila = $resultado->fetch_assoc()) {
            $tipo = $fila['tipo'];
            $fecha_emitida = $fila['fecha_emitida'];
            $foto_frente = $fila['frente'];
            $foto_reverso = $fila['reverso'];
            ?>
            <nav class="lg:h-[40vh] sm:h-[32vh] h-[30vh] w-full flex flex-col justify-center items-center">
                <div class="lg:w-[40%] sm:w-[65%] text-center w-[80%]">
                    <p class="lg:text-[2.5rem] sm:text-[1.9rem] text-[1.6rem]">TIPO <?php echo htmlspecialchars($tipo); ?></p>
                </div>
                <p class="text-[1.25rem] text-[#6c757d]">Tramitada el <?php echo htmlspecialchars($fecha_emitida); ?></p>
            </nav>
            <main class="w-full bg-[#f8f9fa] lg:h-auto sm:py-0 lg:py-[3rem] py-[3rem] sm:h-[68vh] px-[1rem] h-auto flex sm:flex-row flex-col justify-center items-center gap-5 sm:px-[3rem]">
                <div class="bg-[#e3ecfa] lg:w-[421px] sm:w-[310px] px-[2rem] sm:px-0 lg:h-auto sm:h-[80%] border-solid border-[#e2e2e2] rounded-[5px] border-[1px] flex flex-col items-center">
                    <div class="lg:w-[70%] xl:w-[80%] sm:w-[70%] bg-[white] border-solid border-[gray] rounded-[3px] border-[1px]">
                        <img src="<?php echo "../" . htmlspecialchars($foto_frente); ?>" alt="Foto frente">
                    </div>
                    <div class="w-full h-[10vh] flex items-center px-[1rem]">
                        <p>Frente</p>
                    </div>
                </div>
                <div class="bg-[#e3ecfa] lg:w-[421px] sm:w-[310px] px-[2rem] sm:px-0 lg:h-auto sm:h-[80%] border-solid border-[#e2e2e2] rounded-[5px] border-[1px] flex flex-col items-center">
                    <div class="lg:w-[70%] xl:w-[80%] sm:w-[70%] bg-[white] border-solid border-[gray] rounded-[3px] border-[1px]">
                        <img src="<?php echo "../" . htmlspecialchars($foto_reverso); ?>" alt="Foto reverso">
                    </div>
                    <div class="w-full h-[10vh] flex items-center px-[1rem]">
                        <p>Reverso</p>
                    </div>
                </div>
            </main>
